import React, { useContext } from "react";
import "./NavbarAdmin.css";
import { MdLogout } from "react-icons/md"; // Import logout icon
import { IoPersonSharp } from "react-icons/io5";
import { Link, useNavigate } from 'react-router-dom';
import { StoreContext } from '../../context/StoreContext';

const NavbarAdmin = ({ setShowLogin }) => {
  const { token, setToken } = useContext(StoreContext);
  const navigate = useNavigate();

  const logout = () => {
    console.log("Logging out...");
    localStorage.removeItem("token"); // Clear token
    localStorage.removeItem("userRole"); // Clear user role
    setToken(""); // Reset token in context
    navigate("/"); // Redirect to home after logout
  };

  return (
    <div className="navbar">
      <div className="logo" onClick={() => navigate("/")}>CKFood.</div>
      <div className="profile">
        {!token ? (
          <button onClick={() => setShowLogin(true)}>Sign In</button>
        ) : (
          <div className="navbar-profile">
            <IoPersonSharp />
            <ul className="nav-profile-dropdown">
              <li onClick={() => navigate('/myorders')}>Orders</li>
              <hr />
              <li onClick={logout}><MdLogout /> Logout</li>
            </ul>
          </div>
        )}
      </div>
    </div>
  );
};

export default NavbarAdmin;
