import React, { useContext, useEffect } from "react";
import "./NavbarAdmin.css";
import { MdLogout } from "react-icons/md"; // Import logout icon
import { useNavigate } from 'react-router-dom';
import { StoreContext } from '../../context/StoreContext';

const NavbarAdmin = () => {
  const { token, setToken } = useContext(StoreContext);
  const navigate = useNavigate();

  const logout = () => {
    console.log("Current token before logout:", token); // Log before logout
    console.log("Logging out...");
    localStorage.removeItem("token");
    setToken(""); // This will schedule a re-render
    // Note: You cannot log the updated token here directly.
    navigate("/"); // Redirect to home after logout
  };

  // Log token whenever it changes
  useEffect(() => {
    console.log("Current token after update:", token);
  }, [token]);

  return (
    <div className="navbar">
      <div className="logo">CKFood.</div>
      <div className="profile">
        Admin Panel
        <button onClick={logout} style={{ marginLeft: '10px' }}>
          <MdLogout /> Logout
        </button>
      </div>
    </div>
  );
};

export default NavbarAdmin;
