import React, { useState } from 'react'
import './Add.css'
// import { useEffect } from 'react';

const Add = () => {
    const [image, setImage] = useState(false);
    const [data, setData] = useState({
        name:"",
        description:"",
        price:"",
        category:"Breads",
        quantity:""
    });

    const onChangeHandler = (event) => {
        const name = event.target.name;
        const value = event.target.value;
        setData(data =>({...data, [name]:value}))
    }

    // useEffect(()=>{
    //     console.log(data);
    // }, [data])

    const onSubmitHandler = async (event) => {
        event.preventDefault();
        const formData = new FormData();
        formData.append("name", data.name);
        formData.append("name", data.name);
        formData.append("name", data.name);
        formData.append("name", data.name);
        formData.append("name", data.name);
    }

  return (
    <div className='add'>
      <form className='flex-col' onSubmit={onSubmitHandler}>
        <div className='add-image-upload flex-col'>
            <p>Upload Image</p>
            <label htmlFor='image'>
                <img src={image?URL.createObjectURL(image):''}/>
            </label>
            <input onChange={(e)=>setImage(e.target.files[0])} type="file" id="image"/>
        </div>
        <div className='add-product-name flex-col'>
            <p>Product Name</p>
            <input onChange={onChangeHandler} value={data.name} type="text" name='name' placeholder='Type here'/>
        </div>
        <div className='add-product-description flex-col'>
            <p>Product Description</p>
            <textarea onChange={onChangeHandler} value={data.description} name='description' rows="6" placeholder='Write content here'/>
        </div>
        <div className='add-category-price'>
            <div className='add-category flex-col'>
                <p>Product Category</p>
                <select onChange={onChangeHandler} name='category'>
                    <option value='Sides'>Sides</option>
                    <option value='Breads'>Breads</option>
                    <option value='Curries'>Curries</option>
                    <option value='Rice'>Rice</option>
                    <option value='Desserts'>Desserts</option>
                    <option value='Miscellaneous'>Miscellaneous</option>
                </select>
            </div>
            <div className='add-price flex-col'>
                <p>Product Price</p>
                <input onChange={onChangeHandler} value={data.price} type='Number' name='price' placeholder='$20'/>
            </div>
            <div className='add-quantity flex-col'>
                <p>Product Quantity</p>
                <input onChange={onChangeHandler} value={data.quantity} type='Number' name='quantity' placeholder='20'/>
            </div>
        </div>
        <button type='submit' className='add-btn'>Add</button>
      </form>
    </div>
  )
}

export default Add
