import React, { useState, useContext } from "react";
import "./Navbar.css";
import { FaShoppingCart } from "react-icons/fa";
import { IoPersonSharp } from "react-icons/io5";
import { IoBagHandle } from "react-icons/io5";
import { MdLogout } from "react-icons/md";
import { Link, useNavigate } from 'react-router-dom';
import { StoreContext } from '../../context/StoreContext';

const Navbar = () => {
  const [menu, setMenu] = useState("home");
  const [isDropdownOpen, setIsDropdownOpen] = useState(false); // State for dropdown
  const { getTotalCartAmount, token, setToken } = useContext(StoreContext);
  const navigate = useNavigate();

  const logout = () => {
    localStorage.removeItem("token");
    localStorage.removeItem("userRole");
    setToken("");
    navigate("/login");
  };

  const toggleDropdown = () => {
    setIsDropdownOpen((prev) => !prev); // Toggle dropdown visibility
  };

  return (
    <div className="navbar">
      <div onClick={() => navigate("/")} className="logo">CKFood.</div>
      <ul className="navbar-menu">
        <Link to='/'
          onClick={() => setMenu("home")}
          className={menu === "home" ? "active" : ""}
        >
          Home
        </Link>
        <a href='#explore-menu'
          onClick={() => {
            setMenu("profile");
            navigate("/#explore-menu");
          }}
          className={menu === "profile" ? "active" : ""}
        >
          Menu
        </a>
      </ul>
      <div className="navbar-right">
        <div className="navbar-search-icon">
          <Link to='/cart'><FaShoppingCart /></Link>
          <div className={getTotalCartAmount() === 0 ? "" : "dot"}></div>
        </div>
        {!token ? (
          <div onClick={() => navigate('/login')}><button>Sign In</button></div>
        ) : (
          <div className="navbar-profile" onClick={toggleDropdown}>
            <IoPersonSharp />
            {isDropdownOpen && (
              <ul className="nav-profile-dropdown">
                <li onClick={() => { navigate('/myorders'); setIsDropdownOpen(false); }}><IoBagHandle /><p>Orders</p></li>
                <hr/>
                <li onClick={() => { logout(); setIsDropdownOpen(false); }}><MdLogout /><p>Logout</p></li>
              </ul>
            )}
          </div>
        )}
      </div>
    </div>
  );
};

export default Navbar;
