import React, { useState, useEffect } from 'react';
import { Routes, Route, Navigate } from 'react-router-dom';
import Navbar from './components/Navbar/Navbar';
import NavbarAdmin from './components/NavbarAdmin/NavbarAdmin';
import Sidebar from './components/Sidebar/Sidebar';
import Home from './pages/Home/Home';
import Cart from './pages/Cart/Cart';
import PlaceOrder from './pages/PlaceOrder/PlaceOrder';
import MyOrders from './pages/MyOrders/MyOrders';
import Add from './pages/Add/Add';
import List from './pages/List/List';
import Orders from './pages/Orders/Orders';
import Summary from './pages/Summary/Summary';
import LoginPopup from './components/LoginPopup/LoginPopup';
import Footer from './components/Footer/Footer';
import { ToastContainer } from 'react-toastify';
import 'react-toastify/dist/ReactToastify.css';

const App = () => {
  const [showLogin, setShowLogin] = useState(false);
  const [userRole, setUserRole] = useState(null); // Store user role
  const url = "http://localhost:4000";

  useEffect(() => {
    // Check local storage for user role on app load
    const role = localStorage.getItem('userRole');
    if (role) {
      setUserRole(role);
    }
  }, []);

  const handleLogin = (role) => {
    setUserRole(role);
    localStorage.setItem('userRole', role); // Store role on login
  };

  const renderRoutes = () => {
    if (userRole === "Admin") {
      return (
        <>
          <NavbarAdmin />
          <hr />
          <div className='app-content'>
            <Sidebar />
            <Routes>
              <Route path="/add" element={<Add url={url} />} />
              <Route path="/list" element={<List url={url} />} />
              <Route path="/orders" element={<Orders url={url} />} />
              <Route path="/summary" element={<Summary url={url} />} />
              <Route path="*" element={<Navigate to="/add" />} /> {/* Redirect to a default admin page */}
            </Routes>
          </div>
        </>
      );
    } else {
      return (
        <>
          <Navbar setShowLogin={setShowLogin} />
          <Routes>
            <Route path="/" element={<Home />} />
            <Route path="/cart" element={<Cart />} />
            <Route path="/order" element={<PlaceOrder />} />
            <Route path="/myorders" element={<MyOrders />} />
            <Route path="*" element={<Navigate to="/" />} /> {/* Redirect to home page */}
          </Routes>
        </>
      );
    }
  };

  return (
    <>
      {showLogin && <LoginPopup setShowLogin={setShowLogin} handleLogin={handleLogin} />}
      <ToastContainer />
      {renderRoutes()}
      <Footer />
    </>
  );
};

export default App;
