import React, { useContext, useState } from 'react';
import './LoginPopup.css';
import { StoreContext } from '../../context/StoreContext';
import axios from 'axios';

const LoginPopup = ({ setShowLogin }) => {
    const { url, setToken } = useContext(StoreContext);
    const [currState, setCurrState] = useState("Sign Up");
    const [data, setData] = useState({
        name: "",
        phone: "",
        password: ""
    });

    const onChangeHandler = (event) => {
        const name = event.target.name;
        const value = event.target.value;
        setData(data => ({ ...data, [name]: value }));
    };

    const onLogin = async (event) => {
    event.preventDefault(); // Stops the webpage from loading

    let newUrl = url;
    if (currState === "Login") {
        newUrl += "/api/user/login";
    } else {
        newUrl += "/api/user/register";
    }

    const response = await axios.post(newUrl, data);
    if (response.data.success) {
        setToken(response.data.token);
        localStorage.setItem("token", response.data.token);

        // Store user role if logging in
        if (currState === "Login") {
            localStorage.setItem("userRole", response.data.role); // Store the role
            handleLogin(response.data.role); // Update role in App.js state
        }

        setShowLogin(false);

        // Redirect based on role
        if (currState === "Login" && response.data.role === "Admin") {
            window.location.href = '/admin'; // Redirect to admin panel (adjust the path as needed)
        } else {
            window.location.href = '/'; // Redirect to user panel (adjust the path as needed)
        }
    } else {
        alert(response.data.message);
    }
};

    return (
        <div className='login-popup'>
            <form onSubmit={onLogin} className='login-popup-container'>
                <div className='login-popup-title'>
                    <h2>{currState}</h2>
                    <div className='cross' onClick={() => setShowLogin(false)}>X</div>
                </div>
                <div className='login-popup-inputs'>
                    {currState === "Login" ? null : <input name='name' onChange={onChangeHandler} value={data.name} type='text' placeholder='Your name' required />}
                    <input name='phone' onChange={onChangeHandler} value={data.phone} type='phone' placeholder='Your phone number' required />
                    <input name='password' onChange={onChangeHandler} value={data.password} type='password' placeholder='Your Password' required />
                </div>
                <button type='submit'>{currState === "Sign Up" ? "Create account" : "Login"}</button>
                {currState === 'Login' 
                    ? <p>Create a new account? <span onClick={() => setCurrState("Sign Up")}>Click here</span></p> 
                    : <p>Already have an account? <span onClick={() => setCurrState("Login")}>Login here</span></p>}
            </form>
        </div>
    );
};

export default LoginPopup;
